INSERT INTO `Book` (`Id`, `name`, `price`, `author`, `year`, `description`, `image`, `status`, `usedUser`) VALUES
(1, 'Маленький Принц', 79.9, 'Антуан де Сент-Экзюпери', 1943, '«Ма́ленький принц» (фр. Le Petit Prince) — аллегорическая повесть-сказка, наиболее известное произведение Антуана де Сент-Экзюпери.\r\n\r\nСказка рассказывает о Маленьком принце, который посещает различные планеты в космосе, включая Землю. Книга обращается к темам одиночества, дружбы, любви и утраты. Несмотря на стиль детской книги, её герой рассуждает о жизни и человеческой природе[2].\r\n\r\nВпервые опубликована 6 апреля 1943 года в Нью-Йорке. Было продано более 140 миллионов экземпляров по всему миру, что поставило её в ряд самых продаваемых книг.\r\n\r\nРисунки в книге выполнены самим автором и не менее знамениты, чем сама книга. Это не только иллюстрации, но и органическая часть произведения: сами герои сказки ссылаются на рисунки и спорят о них. Уникальные иллюстрации в «Маленьком принце» разрушают языковые барьеры, становятся частью универсального визуального лексикона, понятного каждому.\r\n\r\n«Ведь все взрослые сначала были детьми, только мало кто из них об этом помнит», — Антуан де Сент-Экзюпери, из посвящения к книге.', 'prince.jpg', 'В заказе', 1),
(2, 'Дежавю', 35, 'Олег Нечипоренко', 2022, '\"Дежавю\" – максимально откровенная и провокационная автобиография самого известного российского рэп – артиста KIZARU. Лайфстайл Олега Нечипоренко уже давно ассоциируется с жизнью, о которой тайком мечтают сотни тысяч – стремительная карьера, побег из России, миллионы подписчиков в соцсетях.\r\n\r\nЭта книга честный рассказ об изнанке популярности, предательстве друзей и жизненных уроках. И пусть биография автора неоднозначна, его предприимчивость и талант привели музыканта на вершину российского хип-хопа.\r\n\r\nКнига дополнена редкими иллюстрациями из личного архива KIZARU.', 'dejavu.jpg', 'Не продана', 1),
(3, 'Карты Таро', 20.83, 'BookShop', 2021, 'Таро Уэйта – одна из самых знаменитых колод Таро в мире! Ее создатель – известный исследователь Каббалы и мистических учений Артур Эдвард Уэйт.\r\n\r\nОбразы карт, созданных Уэйтом, настолько просты и понятны, что могут дать правдивый ответ даже новичку в гадании. И в то же время Таро Уэйта – это серьезная эзотерическая практика, которая поможет вам разобраться в себе и своих чувствах, отделить истинные желания от ложных, найти свой путь в жизни, увидеть решение сложной ситуации и понять намерения других людей.', 'taro.jpg', 'В заказе', 1),
(4, 'Цветы для Элджернона', 20, 'Дэниел Киз', 2021, 'Сорок лет назад это считалось фантастикой. Сорок лет назад это читалось как фантастика. Исследующая и расширяющая границы жанра, жадно впитывающая всевозможные новейшие веяния, примеряющая общечеловеческое лицо, отважно игнорирующая каинову печать \"жанрового гетто\". Сейчас это воспринимается как одно из самых человечных произведений новейшего времени, как роман пронзительной психологической силы, как филигранное развитие темы любви и ответственности. Не зря вышедшую уже в 1990-е книгу воспоминаний Киз назвал \"Элджернон, Чарли и я\".', 'flowers.jpg', 'В заказе', 1),
(5, 'Человек бензопила', 46.55, 'Тацуки Фудзимото', 2021, 'Дэндзи – обычный молодой человек, который промышляет охотой на демонов, чтобы расплатиться с долгами покойного отца. Если он хоть раз опоздает с выплатой, его ждет смерть. Дэндзи живет в крошечной лачуге со своим демоническим псом-бензопилой по имени Почита и едва сводит концы с концами. Все о чем он мечтает – ходить на свидания с девушкой и есть хлеб с джемом. Но у судьбы на него другие планы...\r\n\r\nВ один прекрасный день юноша обретает сверхъестественные силы, позволяющие ему превращать свое тело в живое оружие. Дэндзи вырывается из нищеты и под рев дребезжащих бензопил и крики умирающих демонов начинает прорезать себе дорогу в новую жизнь...', 'benzopila.jpg', 'В заказе', 1),
